    //   }

    // componentDidMount() {
    //     fetch('/Coins')
    //     .then(res => res.json())
    //     .then((data) => {
    //       this.setState({ coins: data })
    //     })
    //     .catch(console.log)
    // }

    const cors = 'https://cors-anywhere.herokuapp.com${url}'
    const url = cors + 'http://127.0.0.1:5000/'
    const [coin, setCoin] = useState(null) 

    useEffect(() => {
        axios.get(url)
            .then(response => {
                setCoin(response.data)
            })
    }, [url])

    if(coin) {
        return (
            <div>
            <h1>{coin.id}</h1>
            <Coins coins={this.state.coins} />
            </div>
        )
    }

    return(
        <h1>coin is missing</h1>    
    )
}

export default CoinListItem
