import React, { useEffect, useState } from "react"

function useCoins() {
	const [coins, setCoins] = useState([])

	useEffect(() => {
		fetch("http://127.0.0.1:5000/")
			.then((response) => response.json())
			.then((data) => {
				setCoins(data) 
			})
	}, [])

	console.log(coins);

	return coins
}

export default function CoinListItem() {
	const coins = useCoins() 

	console.log(coins)

	if (coins.length === 0) {
		console.log('ik heb niks')
		return null;	
	}

	return (
		<div>
			<h1>Cool app</h1>
			{coins.length > 0 ? (
				coins.map((coin) => (
					<li>
						<h2>{coin.id}</h2>
						<p>{coin.gains_weekly}</p>
					</li>
				))
			) : (
				<h1>Loading coins...</h1>
			)}
		</div>
	)
}





