import React, { useEffect, useState } from "react"

function useCoins() {
	const [coins, setCoins] = useState([])

	useEffect(() => {
		fetch("http://127.0.0.1:5000/")
			.then((response) => response.json())
			.then((data) => {
				setCoins(data) 
			})
	}, [])

	return coins
}

export default function Example() {
	const posts = usePosts() // new

	return (
		<div>
			<h1>Cool app</h1>
			{posts.length > 0 ? (
				posts.map((item) => (
					<li>
						<h2>{item.title}</h2>
						<p>{item.description}</p>
					</li>
				))
			) : (
				<h1>Loading posts...</h1>
			)}
		</div>
	)
}






