import React, { useEffect, useState } from "react"

export default function CoinListItem() {
    const [posts, setPosts] = useState([])

    useEffect(() => {
		fetch("http://127.0.0.1:5000/")
			.then((response) => response.json())
			.then((data) => {
				setPosts(data) 
			})
	}, [])

	return (
		<div>
			<h1>Cool app</h1>
			{posts.map((item) => (
				<li>
					<h2>{item.title}</h2>
					<p>{item.description}</p>
				</li>
			))}
		</div>
	)
}




